import React, { useState } from "react";

function CreateArea() {
  const [note, setNote] = useState("");

  function createNote(event) {
    const newNote = event.target.value;
    console.log(newNote);
    setNote(newNote);
  }

  return (
    <div>
      <form>
        <input
          name="title"
          placeholder="Title"
          value={note}
          onChange={createNote}
        />
        <textarea name="content" placeholder="Take a note..." rows="3" />
        <button
          onClick={() => {
            addNote(note);
            setNote("");
          }}
        >
          Add
        </button>
      </form>
    </div>
  );
}

export default CreateArea;
